import templeoftheelements.player.*; 

 import templeoftheelements.item.*; 

 import templeoftheelements.collision.*;

 import templeoftheelements.effect.Effect;

 import templeoftheelements.effect.EffectSource;

 public class BludgeonEffect extends Effect { 

                        public BludgeonEffect() {
                            super("Bludgeon");
                        }

                        public float effect(EffectSource source, Object o) {
                            return ((Creature) o).takeDamage(1, "Crushing");
                        }

                        public String getDescription() {
                            return "Deals 1 crushing damage to the target.";
                        }
                     }
